// --- Main Section Component ---

const ClinicNearMeSection = () => {
  const [allClinics, setAllClinics] = useState([]);
  const [filteredClinics, setFilteredClinics] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isBookingModalOpen, setIsBookingModalOpen] = useState(false);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [slideDirection, setSlideDirection] = useState(null);
  const [windowWidth, setWindowWidth] = useState(
    typeof window !== "undefined" ? window.innerWidth : 0
  );

  // Load data from JSON when component mounts
  useEffect(() => {
    try {
      setAllClinics(clinicsData);
      setFilteredClinics(clinicsData);
    } catch (error) {
      console.error("Error loading clinics data:", error);
      setAllClinics([]);
      setFilteredClinics([]);
    }
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query);

    if (!query.trim()) {
      setFilteredClinics(allClinics);
      setCurrentIndex(0);
      return;
    }

    // Filter clinics based on City or Area containing the search query
    const lowercaseQuery = query.toLowerCase();
    const filtered = allClinics.filter(
      (clinic) =>
        clinic.City.toLowerCase().includes(lowercaseQuery) ||
        clinic.Area.toLowerCase().includes(lowercaseQuery) ||
        clinic.Address.toLowerCase().includes(lowercaseQuery)
    );

    setFilteredClinics(filtered);
    setCurrentIndex(0); // Reset
  };

  // Update window width on resize
  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // cards size based on screen size
  const getCardsPerSlide = () => {
    if (windowWidth >= 1024) return 3;
    if (windowWidth >= 640) return 2;
    return 1;
  };

  const getCardSlidePercentage = () => {
    const cardsPerSlide = getCardsPerSlide();
    return 100 / cardsPerSlide;
  };

  const handlePrev = () => {
    if (isTransitioning) return;

    setSlideDirection("left");
    setIsTransitioning(true);

    setTimeout(() => {
      setCurrentIndex((prevIndex) => {
        const newIndex =
          prevIndex === 0
            ? filteredClinics.length - getCardsPerSlide()
            : prevIndex - 1;
        return newIndex >= 0 ? newIndex : 0;
      });

      setTimeout(() => {
        setIsTransitioning(false);
      }, 50);
    }, 300);
  };

  const handleNext = () => {
    if (isTransitioning) return;

    setSlideDirection("right");
    setIsTransitioning(true);

    setTimeout(() => {
      setCurrentIndex((prevIndex) => {
        const maxIndex = filteredClinics.length - getCardsPerSlide();
        const newIndex = prevIndex >= maxIndex ? 0 : prevIndex + 1;
        return newIndex;
      });

      setTimeout(() => {
        setIsTransitioning(false);
      }, 50);
    }, 300);
  };

  // If no results found
  const NoResultsMessage = () => (
    <div className="text-center p-4 bg-white rounded-lg shadow">
      <p className="text-[#231F20]">
        No clinics found in that location. Please try another search.
      </p>
    </div>
  );

  return (
    <div className="bg-[#F5FAFE] sm:bg-white py-4 sm:py-16">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        {/*  Header - desktop */}
        <div className="text-center mb-6 lg:mb-8 font-poppins hidden md:block">
          <h2 className="mx-auto text-2xl sm:text-4xl font-bold text-[#231F20] mb-1 leading-[43.4px] tracking-[0%]">
            Dr Batra's Clinic Near Me
          </h2>
          <p className="mx-auto text-sm sm:text-xl text-[#231F20] font-light leading-[25px] tracking-normal">
            Easy Access to Our Clinic
          </p>
        </div>

        {/* Search Bar - For desktop */}
        <div className="hidden md:block">
          <ClinicSearchBar onSearch={handleSearch} />
        </div>

        {/* Mobile View */}
        <div className="block sm:hidden mx-auto max-w-sm mb-6 ">
          <div className="text-center mb-6">
            <h2 className="text-xl font-bold text-[#231F20] mb-1">
              Dr Batra's Clinic's
            </h2>
            <p className="text-sm text-[#4D4D4D]">Easy Access to Our Clinic</p>
          </div>

          {/* Mobile Search Bar */}
          <div className="w-full mb-6">
            <div className="relative">
              <div className="absolute inset-y-0 left-4 flex items-center pointer-events-none z-10">
                <SearchIcon />
              </div>
              <input
                type="text"
                placeholder="Search by city, area or address"
                className="block w-full bg-white border border-[#DEE2E6] rounded-full py-3 pl-12 pr-4 text-base text-[#231F20] placeholder-[#231F20] shadow-sm focus:outline-none"
                onChange={(e) => handleSearch(e.target.value)}
                value={searchQuery}
              />
            </div>
          </div>

          {/* Mobile Card  */}
          {filteredClinics.length > 0 ? (
            <div className="mb-4 ">
              <div className="bg-white rounded-lg border border-[#DEE2E6] border-solid p-2 flex flex-col overflow-hidden">
                <div className="flex justify-between items-center px-4 pt-4 pb-2">
                  <h4 className="font-semibold text-[#231F20] text-lg font-poppins leading-relaxed tracking-normal">
                    {filteredClinics[currentIndex].Headline}
                  </h4>
                  <div className="flex items-center justify-center gap-1 px-2 py-1">
                    <StarIconFilled />
                    <span className="text-base text-[#231F20] font-roboto font-semibold leading-[16px] tracking-normal">
                      {filteredClinics[currentIndex].Ratings.toFixed(1)}
                    </span>
                  </div>
                </div>

                <div className="border-t border-solid border-[1px] border-[#DEE2E6] mx-4 my-2"></div>

                <div className="flex items-start gap-2 px-4 pb-4 flex-grow min-h-[70px]">
                  <div className="flex-shrink-0 mt-0.5">
                    <LocationPinIcon />
                  </div>
                  <p className="font-poppins text-[#231F20] leading-relaxed font-normal text-sm tracking-normal">
                    {filteredClinics[currentIndex].Address}
                  </p>
                </div>

                <div className="border-t border-solid border-[1px] border-[#DEE2E6] mx-4 my-2"></div>

                <div className="mt-auto">
                  <button
                    className="block w-full bg-[#231F20] hover:bg-opacity-90 transition-all duration-200 text-white text-sm font-poppins font-bold py-2.5 text-center"
                    onClick={() => setIsBookingModalOpen(true)}
                  >
                    Book Consultation
                  </button>
                </div>
              </div>
            </div>
          ) : (
            <NoResultsMessage />
          )}

          {/* Pagination Dots - only on mobile */}
          {filteredClinics.length > 0 && (
            <div className="flex justify-center mt-6 gap-2  sm:hidden">
              {filteredClinics.map((_, i) => (
                <div
                  key={i}
                  className={`w-2 h-2 rounded-full ${
                    i === currentIndex ? "bg-[#1167B1]" : "bg-[#DEE2E6]"
                  }`}
                  onClick={() => setCurrentIndex(i)}
                ></div>
              ))}
            </div>
          )}
        </div>

        {/* Tablet and Desktop Carousel */}
        <div className="hidden sm:block relative">
          {/* Left Arrow */}
          <button
            onClick={handlePrev}
            className="absolute top-1/2 left-[-20px] lg:left-[-40px] transform -translate-y-1/2 z-20 rounded-full flex items-center justify-center text-blue-600 hover:text-blue-800 transition-colors duration-200 filter drop-shadow-md"
            aria-label="Previous clinics"
            disabled={filteredClinics.length === 0}
          >
            <LeftArrowIcon />
          </button>

          {/* Google-style review carousel */}
          <div className="overflow-hidden">
            {filteredClinics.length > 0 ? (
              <div
                className={`flex transition-all duration-300 ease-in-out ${
                  isTransitioning
                    ? slideDirection === "right"
                      ? "translate-x-[-20px]"
                      : "translate-x-[20px]"
                    : ""
                }`}
                style={{
                  transform: `translateX(-${
                    currentIndex * getCardSlidePercentage()
                  }%)`,
                  padding: "0.5rem 0",
                  gap: "1rem",
                }}
              >
                {/* Render all clinic cards in a row */}
                {filteredClinics.map((clinic) => (
                  <div
                    key={clinic.id}
                    className={`${
                      getCardsPerSlide() === 3
                        ? "w-[calc(33.333%-0.67rem)]"
                        : getCardsPerSlide() === 2
                        ? "w-[calc(50%-0.5rem)]"
                        : "w-full"
                    } flex-shrink-0`}
                  >
                    <ClinicCard
                      clinic={clinic}
                      onBookConsultation={() => setIsBookingModalOpen(true)}
                    />
                  </div>
                ))}
              </div>
            ) : (
              <NoResultsMessage />
            )}
          </div>

          {/* pagination dots */}
          {filteredClinics.length > 0 && (
            <div className="flex justify-center mt-20 gap-2 sm:flex md:hidden">
              {filteredClinics
                .slice(0, filteredClinics.length - getCardsPerSlide() + 1)
                .map((_, i) => (
                  <div
                    key={i}
                    className={`h-2 rounded-full cursor-pointer transition-all bg-[#DEE2E6] ${
                      i === currentIndex ? "w-4 bg-[#1167B1]" : "w-2"
                    }`}
                    onClick={() => {
                      setCurrentIndex(i);
                      setIsTransitioning(false);
                    }}
                  ></div>
                ))}
            </div>
          )}

          {/* Right Arrow */}
          <button
            onClick={handleNext}
            className="absolute top-1/2 right-[-20px] lg:right-[-40px] transform -translate-y-1/2 z-20 rounded-full flex items-center justify-center text-blue-600 hover:text-blue-800 transition-colors duration-200 filter drop-shadow-md"
            aria-label="Next clinics"
            disabled={filteredClinics.length === 0}
          >
            <RightArrowIcon />
          </button>
        </div>

        {/* Booking Modal */}
        <BookingModal
          isOpen={isBookingModalOpen}
          onClose={() => setIsBookingModalOpen(false)}
        />

        <style
          dangerouslySetInnerHTML={{
            __html: `
          .duration-300 {
            transition-duration: 300ms;
          }
        `,
          }}
        />
      </div>
    </div>
  );
};

export default ClinicNearMeSection;
